INSERT INTO `wp_posts` VALUES (11,1,'2020-04-17 18:57:13','2020-04-17 18:57:13','<!-- wp:paragraph -->\n<p>Below are some pictures of our gallery and displays. We are always on the lookout for new items to display from St. Charles County veterans. The museum also works to rotate items to have different displays throughout the year.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>If you would like to become a sponsor of a display as well, your name and/or business logo will be prominently displayed. <a href=\"http://stcharlescountyveteransmuseum.org/contact/index.html\">Contact us today</a>!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:gallery {\"ids\":[88,85,86,87,92,93,94,96,98,100],\"columns\":2,\"align\":\"center\"} -->\n<figure class=\"wp-block-gallery aligncenter columns-2 is-cropped\"><ul class=\"blocks-gallery-grid\"><li class=\"blocks-gallery-item\"><figure><img src=\"http://st-charles-county-veterans-museum.local/wp-content/uploads/2020/04/gallery4-300x189.png\" alt=\"\" data-id=\"88\" data-link=\"http://st-charles-county-veterans-museum.local/gallery/gallery4/\" class=\"wp-image-88\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://st-charles-county-veterans-museum.local/wp-content/uploads/2020/04/gallery1-300x189.png\" alt=\"\" data-id=\"85\" data-link=\"http://st-charles-county-veterans-museum.local/gallery/gallery1/\" class=\"wp-image-85\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://st-charles-county-veterans-museum.local/wp-content/uploads/2020/04/gallery2-300x189.png\" alt=\"\" data-id=\"86\" data-link=\"http://st-charles-county-veterans-museum.local/gallery/gallery2/\" class=\"wp-image-86\"/></figure></li><li class=\"blocks-gallery-item\"><figure><img src=\"http://st-charles-county-veterans-museum.local/wp-content/uploads/2020/04/gallery3-300x189.png\" alt=\"\" data-id=\"87\" d